<?php// No direct access to this filedefined('_JEXEC') or die('Restricted access'); // import Joomla view libraryjimport('joomla.application.component.view'); /** * HTML View class for the HelloWorld Component */class HiOrgCalViewHiOrgCal extends JViewLegacy{	// Overwriting JView display method	function display($tpl = null) 	{		                $document =& JFactory::getDocument();                $document->addStyleSheet(JURI::root(true).'/components/com_hiorgcal/css/style.css');                $document->addScript(JURI::root(true).'/components/com_hiorgcal/js/jquery.min.js');                $document->addScript(JURI::root(true).'/components/com_hiorgcal/js/hiorgcal.js');                // Assign data to the view Hole Daten vom Model		$this->msg = $this->get('Dates');                $this->jscript_json = $this->get('Jscript');                $this->instance = $this->getModel();		// Check for errors.		if (count($errors = $this->get('Errors'))) 		{			JError::raiseError(500, implode('<br />', $errors));			return false;		}		// Display the view		parent::display($tpl);	}}